import React from 'react';
import { useForm } from 'react-hook-form';
import emailjs from 'emailjs-com'


function Contact(props) {

    const { register, handleSubmit, formState: { errors } } = useForm();

    function sendEmail(e) {
        e.preventDefault();

        emailjs.sendForm('service_nkdx284', 'template_zy3ugkp', e.target, 'user_JPwNN0pkNzOXzxoio7L8c')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            }); 
    } 

    return (
        <div className="text-center pt-4 pb-4">
            <h3 className="pb-4">Contact me</h3>
            <div className="card form-contact">
                <form onSubmit={sendEmail} className="m-3">
                    <input className="mb-3 form-control" type="text" placeholder="Name" {...register("from_name", {required: true, maxLength: 80})} name="from_name" />
                    <input className="mb-3 form-control" type="email" placeholder="E-mail" {...register("from_email", {required: true, maxLength: 80})} name="from_email" />
                    <textarea className="mb-3 form-control" placeholder="Message" {...register("message", {required: true, min: 10})} name="message"/>

                    <button className="btn btn-primary" type="submit">Send</button>
                </form>

            </div>
        </div>
    );
}

export default Contact;
